<script>
//登录令牌的设置与获取
global.getToken = function() {
	return uni.getStorageSync('auth_token');
};
global.setToken = function(token) {
	if (token) uni.setStorageSync('auth_token', token);
	else uni.removeStorageSync('auth_token');
};

/**
 * 判断是否是微信内置浏览器打开
 */
global.is_weixin = function (){
		// #ifndef H5
			return false
		// #endif
		// #ifdef H5
			let ua = navigator.userAgent.toLowerCase()
			if(ua.match(/MicroMessenger/i)=="micromessenger") {
				return true
			} else {
				return false
			}
		// #endif
}

//消息提示
global.toast = function(msg, callback) {
	uni.showToast({
		title: msg,
		icon: 'none',
		duration: 2000,
		success: function() {
			if (typeof callback == 'function') {
				setTimeout(function() {
					callback();
				}, 2000);
			}
		}
	});
};
	
export default {
	onLaunch: function() {
		global.http = this.$http;
	},
	onShow: function() {
		console.log('App Show');
	},
	onHide: function() {
		console.log('App Hide');
	}
};
</script>

